import React, { useEffect, useState } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

export default function OrderBoard() {
  const [messages, setMessages] = useState(() => {
    // Load messages from localStorage on init, or empty array
    const saved = localStorage.getItem("messages");
    return saved ? JSON.parse(saved) : [];
  });

  const [orders, setOrders] = useState(() => {
    // Load orders from localStorage on init, or empty array
    const saved = localStorage.getItem("orders");
    return saved ? JSON.parse(saved) : [];
  });

  // fetch last order on mount and merge with saved orders
  useEffect(() => {
    fetch("http://localhost:5000/api/orders")
      .then((r) => r.json())
      .then((fetchedOrders) => {
        if (fetchedOrders.length > 0) {
          const lastOrder = fetchedOrders[fetchedOrders.length - 1];

          // Update state with last order if not already present
          setOrders((prevOrders) => {
            // Check if lastOrder.id is already in state to avoid duplicates
            if (prevOrders.some((o) => o.id === lastOrder.id)) {
              return prevOrders;
            }
            return [lastOrder];
          });
        }
      })
      .catch(console.error);
  }, []);

  // save messages to localStorage when messages change
  useEffect(() => {
    localStorage.setItem("messages", JSON.stringify(messages));
  }, [messages]);

  // save orders to localStorage when orders change
  useEffect(() => {
    localStorage.setItem("orders", JSON.stringify(orders));
  }, [orders]);

  // listen to live socket events
  useEffect(() => {
    socket.on("message", (m) => {
      setMessages((prev) => {
        const newMessages = [m, ...prev].slice(0, 50);
        return newMessages;
      });
    });

    socket.on("order", (o) => {
      setOrders((prev) => {
        // Only add if this order is new (avoid duplicates)
        if (prev.length === 0 || prev[0].id !== o.id) {
          return [o];
        }
        return prev;
      });
    });

    return () => {
      socket.off("message");
      socket.off("order");
    };
  }, []);

  // ... your rendering code remains unchanged ...
  return (
    <div
      style={{
        display: "flex",
        gap: 20,
        fontFamily: "Arial, sans-serif",
        padding: 20,
      }}
    >
      {/* Incoming Messages */}
      <div
        style={{
          flex: 1,
          background: "#f9f9f9",
          borderRadius: 8,
          boxShadow: "0 0 8px rgba(0,0,0,0.05)",
        }}
      >
        <h3
          style={{
            padding: "10px 15px",
            borderBottom: "1px solid #ddd",
            margin: 0,
          }}
        >
          Incoming Messages
        </h3>
        <div
          style={{ maxHeight: 500, overflowY: "auto", padding: 15 }}
          aria-live="polite"
        >
          {messages.length === 0 ? (
            <div style={{ color: "#999", fontStyle: "italic" }}>
              No messages yet.
            </div>
          ) : (
            messages.map((m, i) => (
              <div
                key={i}
                style={{
                  marginBottom: 12,
                  paddingBottom: 8,
                  borderBottom: "1px solid #eee",
                }}
              >
                <div
                  style={{
                    fontSize: 12,
                    color: "#666",
                    marginBottom: 4,
                    wordBreak: "break-word",
                  }}
                >
                  {m.from}
                </div>
                <div style={{ fontSize: 14, whiteSpace: "pre-wrap" }}>
                  {m.text}
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Latest Order */}
      <div
        style={{
          width: 380,
          background: "#fefefe",
          borderRadius: 8,
          boxShadow: "0 0 8px rgba(0,0,0,0.05)",
        }}
      >
        <h3
          style={{
            padding: "10px 15px",
            borderBottom: "1px solid #ddd",
            margin: 0,
          }}
        >
          Latest Order
        </h3>
        <div
          style={{ maxHeight: 500, overflowY: "auto", padding: 15 }}
          aria-live="polite"
        >
          {orders.length === 0 ? (
            <div style={{ color: "#999", fontStyle: "italic" }}>
              No orders yet.
            </div>
          ) : (
            orders.map((o) => (
              <div
                key={o.id}
                style={{
                  paddingBottom: 12,
                  borderBottom: "1px solid #eee",
                  wordBreak: "break-word",
                }}
              >
                <div
                  style={{
                    fontWeight: "bold",
                    fontSize: 16,
                    marginBottom: 6,
                  }}
                >
                  #{o.id} — {o.item} × {o.quantity}
                </div>
                <div style={{ color: "#444", marginBottom: 4 }}>
                  From: {o.from}
                </div>
                <div style={{ color: "#444", marginBottom: 4 }}>
                  Total: ₹{o.total}
                </div>
                <div style={{ fontSize: 12, color: "#999" }}>
                  {new Date(o.createdAt).toLocaleString()}
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
